SELECT * FROM sys.dm_external_data_processed
GO

----------------------
---- Cost Control ----
----------------------

-- Check the current state of data processed limits:
SELECT * FROM sys.configurations
WHERE name like 'Data processed %';

-- Set new limits for desired period:
sp_set_data_processed_limit
	@type = N'daily',
	@limit_tb = 1000

sp_set_data_processed_limit
	@type= N'weekly',
	@limit_tb = 2

sp_set_data_processed_limit
	@type= N'monthly',
	@limit_tb = 3334
